import React from "react";
import ReactDOM from "react-dom";
import App from "./App";
//NOTE: this file is being called no matter what.
//so for data, it is being called.
//KEEP THIS IN MIND, because I may need to do a lot of refactoring and such.
//also may have to move stuff to App.js 

const name = "Luke"
ReactDOM.render(<App name={name}/>, document.getElementById("root"));
//checks if the password is 8> characters, has at least 1 capital, 1 lowercase, and 1 number.


//onload: handles setting a few important functions
window.onload = function() {
    document.querySelector('#login').disabled = true
    document.querySelector('#signup').disabled = true
    if(window.localStorage.getItem('username')) window.location.assign('data.html') //do this via routing

}
